import * as React from 'react';
import Prismic from '@prismicio/client';
import { Link, RichText, Date } from 'prismic-reactjs';
import './style.css';
import Loader from './components/Loader';
import { apiEndpoint, accessToken} from '../../data/constants';

const BlogPage = () => {
	const [posts, setPosts] = React.useState([]);
	const [loading, setLoading] = React.useState(false);

	React.useEffect(() => {
		const client = Prismic.client(apiEndpoint, { accessToken });
		setLoading(true);
		const fetchData = async () => {
			try {
				const response = await client.query(
					Prismic.Predicates.at('document.type', 'blog')
				)
				if (response) {
					setPosts(response.results)
				}
			} catch (error) {
				console.error(error);
			} finally {
				setLoading(false);
			}
		}
		fetchData();
	}, [])

	function formateDate()  {

		const date =  Date(posts.data?.date)?.toString();
		const formattedDate = new Intl.DateTimeFormat('en-US',{
			year: 'numeric',
			month: 'short',
			day: '2-digit' }).format(date);
			return formattedDate
	}

if (loading) {
	return <Loader/>
}
// console.log(posts)

return (
		<div className="blogWraper">
			{posts.map(post => (
				<div>
					<h1>{RichText.asText(post.data.title)}</h1>
					<p>{}</p>
					<img src={post.data.logo_page.url}/>
					<p>{RichText.asText(post.data.text)}</p>
				</div>
			))}
		</div>
	)
}

export default React.memo(BlogPage);
