import * as React from 'react';
import Prismic from '@prismicio/client';
import { Link, RichText, Date } from 'prismic-reactjs';
import './style.css';
import Loader from './components/Loader';
import { apiEndpoint, accessToken} from '../../data/constants';

const BlogPage = () => {
	const [posts, setPosts] = React.useState([]);
	const [loading, setLoading] = React.useState(false);

	React.useEffect(() => {
		const client = Prismic.client(apiEndpoint, { accessToken });
		setLoading(true);
		const fetchData = async () => {
			try {
				const response = await client.query(
					Prismic.Predicates.at('document.type', 'blog')
				)
				if (response) {
					setPosts(response.results)
				}
			} catch (error) {
				console.error(error);
			} finally {
				setLoading(false);
			}
		}
		fetchData();
	}, [])

	const formateDate = (date) =>  {
		const data = Date(date)
		console.log(date)
		const formattedDate = Intl.DateTimeFormat('en-US',{
			formatMatche: "basic",
  		year: "numeric",
  		month: "long",
  		day: "numeric"
		}).format(data);

			return formattedDate
	}

if (loading) {
	return <Loader/>
}
console.log(posts)

return (
		<div className="blogWraper">
			{posts.map(post => (
				<div>
					<h1 className='blog_title'>{RichText.asText(post.data.title)}</h1>
					<div className='blog_wraper_subtitle'>
						<p className='blog_subtitle'>ПРОГРАММИРОВАНИЕ</p>
						<p className='blog_dot'>&bull;</p>
						<p className='blog_data'>{formateDate((post.data.date))}</p>
					</div>
					<img src={post.data.logo_page.url}/>
					<p className='blog_text'>{RichText.asText(post.data.text)}</p>
				</div>
			))}
		</div>
	)
}

export default React.memo(BlogPage);
