import * as React from 'react';
import Prismic from '@prismicio/client';
import { Link, RichText } from 'prismic-reactjs';
import './style.css';
import Loader from './components/Loader';
import { apiEndpoint, accessToken} from '../../data/constants';

const BlogPage = () => {
const [posts, setPosts] = React.useState([]);
// console.log(doc)

	React.useEffect(() => {
		const client = Prismic.client(apiEndpoint, { accessToken });
		const fetchData = async () => {
			const response = await client.query(
				Prismic.Predicates.at('document.type', 'blog')
			)
			if (response) {
				setPosts(response.results)
			}
		}
		fetchData();
	}, [])

	const postDate = Date(posts.data);
	console.log(postDate);

return (
	<>
    {posts?.length ? (
        <div className='blogWraper'>
					<h1>{RichText.asText(posts[1].data.title)}</h1>
					<img src={posts[0].data.logo_page.url}/>
					<p>{RichText.asText(posts[1].data.text)}</p>

					<div className='blogWraper'>
						<h1>{RichText.asText(posts[0].data.title)}</h1>
						<p>{RichText.asText(posts[0].data.text)}</p>
        	</div>
        </div>
      ) : 
			<div><Loader /></div>
    }
	</>
	)
}

export default React.memo(BlogPage);
